    const history = useHistory();

    const fetchHandler = async (data) => {

        try {
            await axiosInstance.post("posts", data)
            history.replace("/home")
        } catch {

        }
    }

    return ( 
    <Fragment>
    <Form onOrder={fetchHandler} /> 
    </Fragment>
    );
};

export default Write;
